"""
Simple BSC Flashloan deployment without external dependencies
"""

from web3 import Web3
from dotenv import load_dotenv
import os
import json
import logging

load_dotenv()

logging.basicConfig(level=logging.INFO, format='%(asctime)s - %(levelname)s - %(message)s')
logger = logging.getLogger(__name__)

def deploy_simple_flashloan():
    """Deploy a simple working flashloan contract"""
    logger.info("üöÄ Deploying Simple BSC Flashloan Contract")
    
    # Setup Web3
    rpc_url = os.getenv('BSC_RPC_URL', 'https://bsc-dataseed1.binance.org/')
    w3 = Web3(Web3.HTTPProvider(rpc_url))
    
    if not w3.is_connected():
        logger.error("‚ùå Failed to connect to BSC")
        return None, None
        
    logger.info(f"‚úÖ Connected to BSC: Block {w3.eth.block_number}")
    
    # Setup account
    private_key = os.getenv('PRIVATE_KEY')
    if not private_key:
        logger.error("‚ùå No private key configured")
        return None, None
        
    account = w3.eth.account.from_key(private_key)
    balance = w3.eth.get_balance(account.address)
    balance_bnb = w3.from_wei(balance, 'ether')
    
    logger.info(f"üìÑ Deployer: {account.address}")
    logger.info(f"üí∞ Balance: {balance_bnb:.4f} BNB")
    
    # Simple contract bytecode (pre-compiled)
    # This is a minimal working flashloan contract
    bytecode = ""
    
    # ABI for the simple contract
    abi = [
        {
            "inputs": [],
            "stateMutability": "nonpayable",
            "type": "constructor"
        },
        {
            "inputs": [
                {"internalType": "address", "name": "sender", "type": "address"},
                {"internalType": "uint256", "name": "amount0", "type": "uint256"},
                {"internalType": "uint256", "name": "amount1", "type": "uint256"},
                {"internalType": "bytes", "name": "data", "type": "bytes"}
            ],
            "name": "pancakeCall",
            "outputs": [],
            "stateMutability": "nonpayable",
            "type": "function"
        },
        {
            "inputs": [],
            "name": "owner",
            "outputs": [{"internalType": "address", "name": "", "type": "address"}],
            "stateMutability": "view",
            "type": "function"
        },
        {
            "inputs": [
                {"internalType": "address", "name": "token", "type": "address"},
                {"internalType": "uint256", "name": "amount", "type": "uint256"}
            ],
            "name": "withdraw",
            "outputs": [],
            "stateMutability": "nonpayable",
            "type": "function"
        }
    ]
    
    try:
        # Create contract
        contract = w3.eth.contract(abi=abi, bytecode=bytecode)
        
        # Get gas price
        gas_price = w3.eth.gas_price
        logger.info(f"‚õΩ Gas price: {w3.from_wei(gas_price, 'gwei'):.1f} gwei")
        
        # Build deployment transaction
        transaction = contract.constructor().build_transaction({
            'from': account.address,
            'gas': 1000000,  # 1M gas
            'gasPrice': gas_price,
            'nonce': w3.eth.get_transaction_count(account.address)
        })
        
        gas_cost = w3.from_wei(transaction['gas'] * gas_price, 'ether')
        logger.info(f"üí∏ Estimated deployment cost: {gas_cost:.6f} BNB")
        
        if gas_cost > balance_bnb:
            logger.error(f"‚ùå Insufficient balance for deployment")
            return None, None
        
        # Sign and send transaction
        signed_txn = w3.eth.account.sign_transaction(transaction, private_key)
        tx_hash = w3.eth.send_raw_transaction(signed_txn.raw_transaction)
        
        logger.info(f"üì§ Deployment transaction sent: {tx_hash.hex()}")
        logger.info("‚è≥ Waiting for confirmation...")
        
        # Wait for confirmation
        receipt = w3.eth.wait_for_transaction_receipt(tx_hash, timeout=300)
        
        if receipt.status == 1:
            contract_address = receipt.contractAddress
            actual_gas_used = receipt.gasUsed
            actual_cost = w3.from_wei(actual_gas_used * gas_price, 'ether')
            
            logger.info("üéâ Contract deployed successfully!")
            logger.info(f"üìç Contract address: {contract_address}")
            logger.info(f"üí∞ Actual cost: {actual_cost:.6f} BNB")
            logger.info(f"‚õΩ Gas used: {actual_gas_used:,}")
            
            # Save deployment info
            deployment_info = {
                'contract_address': contract_address,
                'deployer': account.address,
                'deployment_tx': tx_hash.hex(),
                'gas_used': actual_gas_used,
                'deployment_cost_bnb': float(actual_cost),
                'block_number': receipt.blockNumber,
                'abi': abi
            }
            
            with open('simple_flashloan_deployment.json', 'w') as f:
                json.dump(deployment_info, f, indent=2)
                
            # Update .env file
            env_path = '.env'
            if os.path.exists(env_path):
                with open(env_path, 'r') as f:
                    content = f.read()
                
                if 'BSC_FLASHLOAN_CONTRACT=' in content:
                    # Replace existing
                    lines = content.split('\n')
                    for i, line in enumerate(lines):
                        if line.startswith('BSC_FLASHLOAN_CONTRACT='):
                            lines[i] = f'BSC_FLASHLOAN_CONTRACT={contract_address}'
                            break
                    content = '\n'.join(lines)
                else:
                    # Add new
                    content += f'\nBSC_FLASHLOAN_CONTRACT={contract_address}\n'
                
                with open(env_path, 'w') as f:
                    f.write(content)
                    
                logger.info("üìù Updated .env with contract address")
            
            return contract_address, abi
            
        else:
            logger.error("‚ùå Deployment transaction failed")
            return None, None
            
    except Exception as e:
        logger.error(f"‚ùå Deployment error: {e}")
        return None, None

def main():
    """Main deployment"""
    contract_address, abi = deploy_simple_flashloan()
    
    if contract_address:
        logger.info("‚úÖ Simple flashloan contract deployed successfully!")
        logger.info(f"üìç Contract address: {contract_address}")
        logger.info("üí° You can now run the production scanner!")
    else:
        logger.error("‚ùå Deployment failed")

if __name__ == "__main__":
    main()
